00065 static inline float _Uint2Float(
00066     uint16_t x_int, float x_max, float x_min, uint8_t bits)
00067 {
00068   float span = x_max - x_min;
00069   float offset = x_min;
00070   return ((float)x_int) * span / ((float)((1 << bits) - 1)) + offset;
00071 }
00072 
00082 static inline uint16_t _Float2Uint(
00083     float x_float, float x_max, float x_min, uint8_t bits)
00084 {
00085   float span = x_max - x_min;
00086   float offset = x_min;
00087   return (uint16_t)((x_float - offset) * ((float)((1 << bits) - 1)) / span);
00088 }
