00097 DM_J4310::DM_J4310(uint8_t id, const OptionalParams& optinal_params)
00098 {
00099   /* 变量检查 */
00100 #pragma region
00101   HW_ASSERT(1 <= id && id <= 10, "Error id: %d", id);
00102   HW_ASSERT(
00103       optinal_params.dir == kDirFwd || optinal_params.dir == kDirRev,
00104       "Error dir: %d", optinal_params.dir);
00105   HW_ASSERT(
00106       optinal_params.angle_range == kAngleRange0To2Pi ||
00107           optinal_params.angle_range == kAngleRangeNegInfToPosInf ||
00108           optinal_params.angle_range == kAngleRangeNegPiToPosPi,
00109       "Error angle range: %d", optinal_params.angle_range);
00110   HW_ASSERT(
00111       optinal_params.input_type == kInputTypeRaw ||
00112           optinal_params.input_type == kInputTypeTorq ||
00113           optinal_params.input_type == kInputTypeCurr,
00114       "Error input type: %d", optinal_params.input_type);
00115   HW_ASSERT(
00116       optinal_params.ex_redu_rat > 0,
00117       "Error external reduction ration: %f", optinal_params.ex_redu_rat);
00118   HW_ASSERT(
00119       optinal_params.max_torq_input_lim > 0,
00120       "Error max torque input limit: %f", optinal_params.max_torq_input_lim);
00121   HW_ASSERT(
00122       optinal_params.max_curr_input_lim > 0,
00123       "Error max current input limit: %f", optinal_params.max_curr_input_lim);
00124 #pragma endregion
00125 
00126   motor_info_ = kDM_J4310MotorBaseInfo;
00127 
00128   /* 根据 ID 设定报文 ID */
00129   motor_info_.tx_id = kTx0_ + id;
00130   motor_info_.rx_id = kRx0_ + id;
00131   motor_info_.id = id;
00132 
00133   motor_info_.dir = optinal_params.dir;
00134   motor_info_.angle_range = optinal_params.angle_range;
00135   motor_info_.input_type = optinal_params.input_type;
00136   motor_info_.angle_offset = optinal_params.angle_offset;
00137   /* 转子端力矩限制 */
00138   float rotor_torq_lim = motor_info_.torq_input_lim / motor_info_.redu_rat;
00139   if (optinal_params.remove_build_in_reducer) {
00140     motor_info_.redu_rat = optinal_params.ex_redu_rat;
00141   } else {
00142     motor_info_.redu_rat *= optinal_params.ex_redu_rat;
00143   }
00144 
00145   /* 计算输入限制 */
00146   float max_torq_input = rotor_torq_lim * motor_info_.redu_rat;
00147   if (optinal_params.max_torq_input_lim != std::numeric_limits<float>::max()) {
00148     max_torq_input = hello_world::Min(max_torq_input, optinal_params.max_torq_input_lim);
00149   }
00150   float max_curr_input = motor_info_.curr_input_lim;
00151   if (optinal_params.max_curr_input_lim != std::numeric_limits<float>::max()) {
00152     max_curr_input = hello_world::Min(max_curr_input, optinal_params.max_curr_input_lim);
00153   }
00154 
00155   max_torq_input = hello_world::Min(max_torq_input, kMaxTorq);
00156   max_torq_input = hello_world::Min(max_torq_input, curr2torq(max_curr_input));
00157   motor_info_.raw_input_lim = kInvalidValue;
00158   motor_info_.torq_input_lim = max_torq_input;
00159   motor_info_.curr_input_lim = torq2curr(max_torq_input);
00160 
00161   is_start_ = false;
00162 }
