00164 State DM_J4310::setInput(float input)
00165 {
00166   float torq_input = 0;
00167   switch (motor_info_.input_type) {
00168     case kInputTypeRaw:
00169       torq_input = raw2torq(input);
00170       torq_input_ = hello_world::Bound(
00171           input, motor_info_.torq_input_lim, -motor_info_.torq_input_lim);
00172       curr_input_ = raw2torq(raw_input_);
00173       raw_input_ = torq2raw(torq_input_);
00174       if (fabsf(torq_input_) < fabsf(torq_input)) {
00175         return kStateInputValueOverflow;
00176       } else {
00177         return kStateOk;
00178       }
00179     case kInputTypeTorq:
00180       torq_input_ = hello_world::Bound(
00181           input, motor_info_.torq_input_lim, -motor_info_.torq_input_lim);
00182       raw_input_ = torq2raw(torq_input_);
00183       curr_input_ = torq2curr(torq_input_);
00184       if (fabsf(torq_input_) < fabsf(input)) {
00185         return kStateInputValueOverflow;
00186       } else {
00187         return kStateOk;
00188       }
00189     case kInputTypeCurr:
00190       curr_input_ = hello_world::Bound(
00191           input, motor_info_.curr_input_lim, -motor_info_.curr_input_lim);
00192       raw_input_ = curr2raw(curr_input_);
00193       torq_input_ = curr2torq(torq_input_);
00194       if (fabsf(curr_input_) < fabsf(input)) {
00195         return kStateInputValueOverflow;
00196       } else {
00197         return kStateOk;
00198       }
00199     default:
00200       return kStateInputTypeError;
00201   }
00202 }
