00213 State DM_J4310::encode(uint8_t tx_data[8], uint32_t tx_msg_std_id) const
00214 {
00215   /* 变量检查 */
00216 #pragma region
00217   HW_ASSERT(tx_data, "Error pointer");
00218 #pragma endregion
00219 
00220   if (tx_msg_std_id != motor_info_.tx_id) {
00221     return kStateIdDismatch;
00222   } else {
00223     if (is_start_) {
00224       uint16_t input =
00225           hello_world::Bound(static_cast<uint16_t>(raw_input_),
00226                              static_cast<uint16_t>((0x01 << kTorqBits) - 1),
00227                              static_cast<uint16_t>(0));
00228       memset(tx_data, 0x00, sizeof(uint8_t) * 7);
00229       tx_data[6] |= (input >> 8) & 0x0F;
00230       tx_data[7] = input & 0xFF;
00231     } else {
00232       memset(tx_data, 0xFF, sizeof(uint8_t) * 7);
00233       tx_data[7] = 0xFC;
00234     }
00235 
00236     return kStateOk;
00237   }
00238 }
