00248 State DM_J4310::decode(const uint8_t rx_data[8], uint32_t rx_msg_std_id)
00249 {
00250   /* 变量检查 */
00251 #pragma region
00252   HW_ASSERT(rx_data, "Error pointer");
00253 #pragma endregion
00254 
00255   if (rx_msg_std_id != motor_info_.rx_id) {
00256     return kStateIdDismatch;
00257   } else {
00258     is_start_ = true;
00259     uint16_t raw_angle = static_cast<uint16_t>((rx_data[1] << 8) | rx_data[2]);
00260     float angle = _Uint2Float(raw_angle, kMaxAngle, -kMaxAngle, kAngleBits);
00261     float last_angle = _Uint2Float(
00262         last_raw_angle_, kMaxAngle, -kMaxAngle, kAngleBits);
00263 
00264     /* 判断是否旋转了一圈 */
00265     float round = round_;
00266     float delta_angle = angle - last_angle;
00267     if (fabsf(delta_angle) > 2 * PI * kCross0ValueThres) {
00268       delta_angle < 0 ? round++ : round--;
00269 
00270       /* 避免圈数溢出 */
00271       if (motor_info_.angle_range != kAngleRangeNegInfToPosInf) {
00272         round = fmodf(round, motor_info_.redu_rat);
00273       }
00274     }
00275 
00276     float actual_ang =
00277         static_cast<float>(motor_info_.dir) * (angle + round * 2 * PI) /
00278         motor_info_.redu_rat;
00279     float raw = static_cast<uint16_t>((rx_data[4] << 8) | rx_data[5]);
00280 
00281     /* 统一对电机状态赋值 */
00282     round_ = round;
00283     actual_angle_ = normAngle(actual_ang);
00284     angle_ = normAngle(actual_ang - motor_info_.angle_offset);
00285     vel_ = static_cast<float>(motor_info_.dir) *
00286            _Uint2Float(static_cast<int16_t>((rx_data[2] << 8) | rx_data[3]),
00287                        kMaxVel, -kMaxVel, kVelBits) /
00288            motor_info_.redu_rat;
00289     torq_ = static_cast<float>(motor_info_.dir) * raw2torq(raw);
00290     curr_ = static_cast<float>(motor_info_.dir) * raw2curr(raw);
00291     temp_ = rx_data[6];
00292 
00293     last_raw_angle_ = raw_angle;
00294 
00295     return kStateOk;
00296   }
00297 }
