00238   inline float calc(float err, float last_inte_out)
00239   {
00240     float ratio = 1;
00241     if (isEnabled()) {
00242       if (err * last_inte_out > 0 || last_inte_out < lower_ ||
00243           last_inte_out > upper_) {
00244         ratio = 0;
00245       }
00246     }
00247     return ratio;
00248   };
