00431 class __PACKED_PID_PARAMS InteChangingRate
00432 {
00433  public:
00443   InteChangingRate(bool is_enabled = false, float lower = 0.01,
00444                    float upper = 0.02)
00445   {
00446     setParams(is_enabled, lower, upper);
00447   };
00448 
00457   inline State setEnabled(bool is_enabled)
00458   {
00459     is_enabled_ = is_enabled;
00460     return kStateOk;
00461   }
00462 
00471   inline State setLower(float lower)
00472   {
00473     lower_ = lower;
00474     return handleIllegalParams() ? kStateError : kStateOk;
00475   }
00476 
00485   inline State setUpper(float upper)
00486   {
00487     upper_ = upper;
00488     return handleIllegalParams() ? kStateError : kStateOk;
00489   }
00490 
00501   inline State setParams(bool is_enabled, float lower, float upper)
00502   {
00503     is_enabled_ = is_enabled;
00504     lower_ = lower;
00505     upper_ = upper;
00506     return handleIllegalParams() ? kStateError : kStateOk;
00507   }
00508 
00516   inline bool isLegal() { return 0 <= lower_ && lower_ < upper_; };
00517 
00525   inline bool isEnabled() { return is_enabled_ && isLegal(); };
00526 
00546   inline float calc(float err)
00547   {
00548     float ratio = 1;
00549     if (isEnabled()) {
00550       float fabs_err = err < 0 ? -err : err;
00551       if (fabs_err > upper_) {
00552         ratio = 0;
00553       } else if (fabs_err > lower_) {
00554         ratio = (upper_ - fabs_err) / (upper_ - lower_);
00555       }
00556     }
00557     return ratio;
00558   };
00559 
00560  private:
00561   bool is_enabled_;  
00562   float lower_;      
00563   float upper_;      
00564 
00574   inline bool handleIllegalParams()
00575   {
00576     bool with_illegal_params = false;
00577     if (lower_ < 0) {
00578       lower_ = 0;
00579       with_illegal_params = true;
00580     }
00581 
00582     if (lower_ > upper_) {
00583       upper_ = lower_ + 0.001f;
00584       with_illegal_params = true;
00585     }
00586     return with_illegal_params;
00587   };
00588 };
