00718 
00736   inline float calc(float diff_out, float last_diff_out)
00737   {
00738     float res = diff_out;
00739     if (isEnabled()) {
00740       float delta_diff_out = diff_out - last_diff_out;
00741       if (delta_diff_out <= lower_ || delta_diff_out >= upper_) {
00742         res = weight_ * last_diff_out + (1 - weight_) * diff_out;
00743       }
00744     }
00745     return res;
00746   };
00747 
00748  private:
00749   bool is_enabled_;  
00750   float lower_;      
00751   float upper_;      
00752   float weight_;     
00753 
00759   inline bool handleIllegalParams()
00760   {
00761     bool with_illegal_params = false;
00762     if (lower_ > upper_) {
00763       float tmp = upper_;
00764       upper_ = lower_;
00765       lower_ = tmp;
00766       with_illegal_params = true;
00767     }
00768     if (weight_ < 0) {
00769       weight_ = 0;
00770       with_illegal_params = true;
00771     };
00772     if (weight_ > 1) {
00773       weight_ = 1;
00774       with_illegal_params = true;
00775     }
00776     return with_illegal_params;
00777   };
