00791 class __PACKED_PID_PARAMS DiffPrevious
00792 {
00793  public:
00798   DiffPrevious(float weight = 0.5) { setParams(weight); };
00799 
00805   inline State setEnabled(float weight) { return setWeight(weight); }
00806 
00814   inline State setWeight(float weight)
00815   {
00816     weight_ = weight;
00817     return handleIllegalParams() ? kStateError : kStateOk;
00818   }
00819 
00827   inline State setParams(float weight) { return setWeight(weight); }
00828 
00834   inline bool isLegal() { return 0 <= weight_ && weight_ <= 1; };
00835 
00840   inline bool isEnabled() { return isLegal() && weight_ != 0; };
00841 
00860   inline float calc(float fdb, float last_fdb, float err, float last_err)
00861   {
00862     float diff = err - last_err;
00863     if (isEnabled()) {
00864       diff = (1 - weight_) * diff + weight_ * (fdb - last_fdb);
00865     }
00866     return diff;
00867   }
00868 
00869  private:
00870   float weight_;  
00871 
00876   bool handleIllegalParams()
00877   {
00878     bool with_illegal_params = false;
00879     if (weight_ < 0) {
00880       weight_ = 0;
00881       with_illegal_params = true;
00882     } else if (weight_ > 1) {
00883       weight_ = 1;
00884       with_illegal_params = true;
00885     }
00886     return with_illegal_params;
00887   };
00888 };
