00996   inline float calc(float err)
00997   {
00998     float res = err;
00999     if (isEnabled()) {
01000       if (err > upper_) {
01001         res = err - upper_;
01002       } else if (err < lower_) {
01003         res = err - lower_;
01004       } else {
01005         res = 0;
01006       }
01007     }
01008     return res;
01009   };
01010 
01011  private:
01012   bool is_enabled_;  
01013   float lower_;      
