01211 class __PACKED_PID_PARAMS OutLimit
01212 {
01213  public:
01214   OutLimit(float lower = -0.1f, float upper = 0.1f)
01215       : lower_(lower), upper_(upper)
01216   {
01217     // 如果下限大于上限，则交换两者的值
01218     if (lower > upper) {
01219       float tmp = upper;
01220       upper = lower;
01221       lower = tmp;
01222     }
01223   };
01224   inline State setLower(float lower)
01225   {
01226     lower_ = lower;
01227     return handleIllegalParams() ? kStateError : kStateOk;
01228   }
01229   inline State setUpper(float upper)
01230   {
01231     upper_ = upper;
01232     return handleIllegalParams() ? kStateError : kStateOk;
01233   }
01234   inline State setParams(float lower, float upper)
01235   {
01236     lower_ = lower;
01237     upper_ = upper;
01238     return handleIllegalParams() ? kStateError : kStateOk;
01239   }
01240 
01241   inline bool isLegal() { return lower_ <= upper_; };
01242 
01243   inline float calc(float out)
01244   {
01245     if (!isLegal()) {
01246       handleIllegalParams();
01247     }
01248     if (out > upper_) {
01249       return upper_;
01250     } else if (out < lower_) {
01251       return lower_;
01252     } else {
01253       return out;
01254     }
01255   };
01256 
01257  private:
01258   float lower_;  
01259   float upper_;  
01264   inline bool handleIllegalParams()
01265   {
01266     bool with_illegal_params = false;
01267     if (lower_ > upper_) {
01268       float tmp = upper_;
01269       upper_ = lower_;
01270       lower_ = tmp;
01271       with_illegal_params = true;
01272     }
01273     return with_illegal_params;
01274   };
