00122 template <typename T>
00123 bool Referee::encodeFrame(const T& data, const FrameCmdId frame_cmd_id, const uint16_t sub_content_id, const uint16_t receiver_id, uint8_t* p_frame, size_t& frame_length)
00124 {
00125     bool process_result = false;
00126     static_assert(sizeof(T) + kFramePartLengthExceptData <= kRefereeMaxBuffer, "Frame data size is too large.");
00127     size_t data_index = kFramePartIndexData;
00128     tx_info_.header.frame_header.sof = kRefereeFrameHeaderSof;
00129     tx_info_.header.frame_header.data_length = sizeof(T);
00130     tx_info_.header.frame_header.sequence_number++;
00131     tx_info_.header.frame_cmd_id = frame_cmd_id;
00132     if (frame_cmd_id == kFrameCmdIdInterAmongRobots) {
00133         static_assert(sizeof(T) + kFramePartLengthExceptData + sizeof(InterFrameHeader) <= kRefereeMaxBuffer, "InterAmongRobots Frame data size is too large.");
00134         tx_info_.header.frame_header.data_length += sizeof(InterFrameHeader);
00135         data_index += sizeof(InterFrameHeader);
00136         tx_info_.header.inter_frame_header.sub_content_id = sub_content_id;
00137         tx_info_.header.inter_frame_header.sender_id = robot_performance_.data().robot_id;
00138         if (sub_content_id == kUiSubCmdIdDeleteLayer || sub_content_id == kUiSubCmdIdDrawCharacter || sub_content_id == kUiSubCmdIdDrawGraphic1 || sub_content_id == kUiSubCmdIdDrawGraphic2 || sub_content_id == kUiSubCmdIdDrawGraphic5 || sub_content_id == kUiSubCmdIdDrawGraphic7) {
00139             tx_info_.header.inter_frame_header.receiver_id = tx_info_.header.inter_frame_header.sender_id | 0x0100;
00140         } else {
00141             tx_info_.header.inter_frame_header.receiver_id = receiver_id;
00142         }
00143     }
00144     memcpy(&tx_info_.frame_raw[data_index], &data, sizeof(T));
00145     size_t _frame_length = data_index + sizeof(T) + sizeof(Crc16_t);
00146     process_result = encodeSetCrc(tx_info_.frame_raw, _frame_length);
00147     if (process_result) {
00148         frame_length = _frame_length;
00149         memcpy(p_frame, tx_info_.frame_raw, frame_length);
00150     };
00151 
00152     return process_result;
00153 };
