00105 bool VerifyCrc8CheckSum(uint8_t *p_message, uint32_t length)
00106 {
00107     uint8_t expected_data = 0;
00108 
00109     // Check for valid input parameters.
00110     if ((p_message == 0) || (length <= 2)) {
00111         return false;
00112     }
00113 
00114     // Calculate the expected CRC8 checksum using GetCrc8CheckSum function.
00115     expected_data = GetCrc8CheckSum(p_message, length - 1, kCrc8Init);
00116 
00117     // Verify if the calculated checksum matches the last byte of the message.
00118     return (bool)(expected_data == p_message[length - 1]);
