00172 uint8_t GetCrc8CheckSum(uint8_t *p_message, uint32_t length, uint8_t crc8_init)
00173 {
00174     uint8_t crc_index;
00175     uint8_t *msg = p_message;
00176     uint8_t crc8 = crc8_init;
00177 
00178     // Check for a NULL message.
00179     if (msg == NULL) {
00180         return 0xFF;
00181     }
00182 
00183     // Iterate through the message to calculate the CRC8 checksum.
00184     for (uint32_t i = 0; i < length; i++) {
00185         crc_index = crc8 ^ msg[i];
00186         crc8 = kCrc8Table[crc_index];
00187     }
00188     return crc8;
00189 };
