00191 uint16_t GetCrc16CheckSum(uint8_t *p_message, uint32_t length, uint16_t crc16_init)
00192 {
00193     uint8_t data;
00194     uint8_t crc_index;
00195     uint8_t *msg = p_message;
00196     uint16_t crc16 = crc16_init;
00197 
00198     // Check for a NULL message.
00199     if (msg == NULL) {
00200         return 0xFFFF;
00201     }
00202 
00203     // Iterate through the message to calculate the CRC16 checksum.
00204     for (uint32_t i = 0; i < length; i++) {
00205         data = msg[i];
00206         crc_index = ((uint16_t)(crc16) ^ (uint16_t)(data)) & 0x00ff;
00207         crc16 = ((uint16_t)(crc16) >> 8) ^ kCrc16Table[crc_index];
00208     }
00209 
00210     return crc16;
00211 };
